import { Injectable } from '@angular/core';
import Swal from 'sweetalert2';

@Injectable({
  providedIn: 'root',
})
export class ErrorsService {
  constructor() {}

  async errorHandling(error: any) {
    console.log(
      'DEVELOPER_ERROR : ',
      error['ERROR'].DEVELOPER_ERROR,
      error['ERROR'].DISPLAY_ERROR
    );
    Swal.fire({
      text:
        error['ERROR'].DISPLAY_ERROR +
        ' \n  Something went wrong, please try again !!',
      icon: 'error',
      title: 'Server error !!',
      showConfirmButton: false,
      timer: 5500,
    });
  }

  async errorHandlingHttp(error: any) {
    Swal.fire({
      text: error.message + ' \n Something went wrong, please try again !!',
      icon: 'error',
      title: 'Server error !!',
      showConfirmButton: false,
      timer: 5500,
    });
  }
}
